import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { Document, Page, pdfjs } from 'react-pdf';
import 'react-pdf/dist/Page/AnnotationLayer.css';
import 'react-pdf/dist/Page/TextLayer.css';

// PDF worker
pdfjs.GlobalWorkerOptions.workerSrc = '/pdf.worker.min.mjs';

// Home page component - for file upload
function HomePage() {
  const navigate = useNavigate();

  const handleFileUpload = async (event) => {
    const formData = new FormData();
    const file = event.target.files[0];
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();

      // Navigate to PDF viewer page with filename
      const filename = file.name;
      navigate(`/${filename}`, { 
        state: { 
          fileUrl: `http://localhost:5000${data.filePath}`,
          originalName: filename,
          uploadedFilename: data.filePath.split('/').pop()
        }
      });

    } catch (error) {
      console.error('Upload failed:', error);
    }
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1>BookRead AI</h1>
      <p>Upload a PDF to start reading and asking AI questions</p>

      <div style={{ margin: '40px 0' }}>
        <input 
          type="file" 
          accept=".pdf" 
          onChange={handleFileUpload} 
          style={{ 
            padding: '10px',
            fontSize: '16px',
            border: '2px solid #ddd',
            borderRadius: '8px'
          }}
        />
      </div>
    </div>
  );
}

// PDF Viewer page component - for viewing PDF and AI interaction
function PDFViewerPage() {
  const location = useLocation();
  const navigate = useNavigate();

  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [selectedText, setSelectedText] = useState('');
  const [aiQuery, setAiQuery] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [showTooltip, setShowTooltip] = useState(false);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  // Get file info from navigation state
  const fileUrl = location.state?.fileUrl;
  const originalName = location.state?.originalName;

  if (!fileUrl) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <p>No PDF file found. Please upload a file first.</p>
        <button onClick={() => navigate('/')}>Go Back</button>
      </div>
    );
  }

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setPageNumber(1);
  };

  const handleTextSelection = (e) => {
    console.log('Text selection triggered'); // Debug log

    // Small delay to ensure selection is complete
    setTimeout(() => {
      const selection = window.getSelection();
      const selectedText = selection.toString().trim();

      console.log('Selected text:', selectedText); // Debug log

      if (selectedText.length > 0) {
        try {
          // Get selection position for tooltip
          const range = selection.getRangeAt(0);
          const rect = range.getBoundingClientRect();

          console.log('Selection position:', rect); // Debug log

          setTooltipPosition({
            x: rect.left + (rect.width / 2),
            y: rect.top - 10 + window.scrollY // Add scroll offset
          });
          setShowTooltip(true);
        } catch (error) {
          console.error('Error getting selection position:', error);
        }
      } else {
        setShowTooltip(false);
      }
    }, 100);
  };

  const handleCopy = () => {
    const selection = window.getSelection();
    navigator.clipboard.writeText(selection.toString());
    setShowTooltip(false);
  };

  const handleAskAI = () => {
    const selection = window.getSelection();
    setSelectedText(selection.toString());
    setShowTooltip(false);
    // Focus on the AI query input
    setTimeout(() => {
      const input = document.querySelector('.ai-query-input');
      if (input) input.focus();
    }, 100);
  };

  const handleClickOutside = (e) => {
    if (!e.target.closest('.selection-tooltip')) {
      setShowTooltip(false);
    }
  };

const queryAI = async () => {
  if (!selectedText || !aiQuery) {
    alert('Please select text and enter a query');
    return;
  }

  try {
    const response = await fetch('http://localhost:5000/api/query', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        text: selectedText,
        prompt: aiQuery,
        filename: location.state.uploadedFilename,
        filePath: new URL(fileUrl).pathname, // Get `/uploads/xyz.pdf`
        pageNumber: pageNumber
      })
    });

    const data = await response.json();
    setAiResponse(data.response);
  } catch (error) {
    console.error('AI query failed:', error);
    setAiResponse('Error: Failed to query AI');
  }
};


  return (
    <div style={{ padding: '20px' }} onClick={handleClickOutside}>
      <div style={{ marginBottom: '20px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>üìñ {originalName}</h2>
        <button 
          onClick={() => navigate('/')}
          style={{ padding: '8px 16px', backgroundColor: '#666', color: 'white', border: 'none', borderRadius: '4px' }}
        >
          ‚Üê Upload New PDF
        </button>
      </div>

      {/* Selection Tooltip */}
      {showTooltip && (
        <div 
          className="selection-tooltip"
          style={{
            position: 'absolute',
            left: tooltipPosition.x,
            top: tooltipPosition.y,
            transform: 'translateX(-50%) translateY(-100%)',
            backgroundColor: '#333',
            color: 'white',
            padding: '8px',
            borderRadius: '6px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.3)',
            zIndex: 1000,
            display: 'flex',
            gap: '8px',
            pointerEvents: 'auto'
          }}
        >
          <button
            onClick={handleCopy}
            style={{
              padding: '4px 8px',
              backgroundColor: '#555',
              color: 'white',
              border: 'none',
              borderRadius: '3px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            üìã Copy
          </button>
          <button
            onClick={handleAskAI}
            style={{
              padding: '4px 8px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '3px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            ü§ñ Ask AI
          </button>
        </div>
      )}

      <div style={{ display: 'flex', gap: '20px' }}>
        {/* PDF Viewer */}
        <div style={{ flex: 1 }}>
          <Document
            file={fileUrl}
            onLoadSuccess={onDocumentLoadSuccess}
            onLoadError={(error) => console.error('PDF Error:', error)}
          >
            <div 
              onMouseUp={handleTextSelection}
              onTouchEnd={handleTextSelection}
              style={{ userSelect: 'text', cursor: 'text' }}
            >
              <Page 
                pageNumber={pageNumber}
                width={600}
              />
            </div>
          </Document>

          {numPages && (
            <div style={{ textAlign: 'center', margin: '20px 0' }}>
              <button 
                onClick={() => setPageNumber(pageNumber - 1)}
                disabled={pageNumber <= 1}
                style={{ margin: '0 10px', padding: '8px 16px', backgroundColor: '#2196F3', color: 'white', border: 'none', borderRadius: '4px' }}
              >
                Previous
              </button>

              <span style={{ margin: '0 20px' }}>
                Page {pageNumber} of {numPages}
              </span>

              <button 
                onClick={() => setPageNumber(pageNumber + 1)}
                disabled={pageNumber >= numPages}
                style={{ margin: '0 10px', padding: '8px 16px', backgroundColor: '#2196F3', color: 'white', border: 'none', borderRadius: '4px' }}
              >
                Next
              </button>
            </div>
          )}
        </div>

        {/* AI Panel */}
        <div style={{ width: '300px', padding: '20px', backgroundColor: '#f9f9f9', borderRadius: '8px' }}>
          <h3>ü§ñ Ask AI</h3>

          {selectedText && (
            <div style={{ marginBottom: '15px' }}>
              <strong>Selected Text:</strong>
              <div style={{ padding: '10px', backgroundColor: '#e8f4f8', borderRadius: '4px', fontSize: '14px', maxHeight: '100px', overflow: 'auto' }}>
                {selectedText}
              </div>
            </div>
          )}

          <input
            type="text"
            className="ai-query-input"
            placeholder="Ask a question about the selected text..."
            value={aiQuery}
            onChange={(e) => setAiQuery(e.target.value)}
            style={{ width: '100%', padding: '10px', marginBottom: '10px', borderRadius: '4px', border: '1px solid #ddd' }}
          />

          <button
            onClick={queryAI}
            disabled={!selectedText || !aiQuery}
            style={{ width: '100%', padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
          >
            Ask AI
          </button>

          {aiResponse && (
            <div style={{ marginTop: '15px' }}>
              <strong>AI Response:</strong>
              <div style={{ padding: '10px', backgroundColor: '#fff', borderRadius: '4px', border: '1px solid #ddd', fontSize: '14px' }}>
                {aiResponse}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

