import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import 'react-pdf/dist/Page/AnnotationLayer.css';
import 'react-pdf/dist/Page/TextLayer.css';

// PDF worker
pdfjs.GlobalWorkerOptions.workerSrc = '/pdf.worker.min.mjs';

function App() {
  const [fileUrl, setFileUrl] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [originalFileName, setOriginalFileName] = useState(''); // Store original filename

  const handleFileUpload = async (event) => {
    const formData = new FormData();
    const file = event.target.files[0];
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();

      // Store both URLs - one for react-pdf, one for clean new tab
      const reactPdfUrl = `http://localhost:5000${data.filePath}`;
      setFileUrl(reactPdfUrl);
      setOriginalFileName(file.name); // Store original filename

      console.log('File uploaded:', reactPdfUrl);
    } catch (error) {
      console.error('Upload failed:', error);
    }
  };

  // Function to open PDF in new tab with clean URL
  const openPDFInNewTab = () => {
    if (fileUrl && originalFileName) {
      const cleanUrl = `http://localhost:5000/pdf/${originalFileName}`;
      window.open(cleanUrl, '_blank');
    } else {
      alert('Please upload a PDF file first');
    }
  };

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setPageNumber(1);
    console.log('PDF loaded, pages:', numPages);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>BookRead AI - PDF Viewer</h1>

      <div style={{ marginBottom: '20px' }}>
        <input 
          type="file" 
          accept=".pdf" 
          onChange={handleFileUpload} 
          style={{ marginRight: '10px' }}
        />

        {/* New Tab Button */}
        <button 
          onClick={openPDFInNewTab}
          disabled={!fileUrl}
          style={{ 
            margin: '10px', 
            padding: '8px 16px', 
            backgroundColor: fileUrl ? '#4CAF50' : '#ccc',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: fileUrl ? 'pointer' : 'not-allowed',
            fontSize: '14px'
          }}
        >
          📄 Open PDF in New Tab
        </button>
      </div>

      {fileUrl && (
        <div style={{ border: '2px solid #ddd', padding: '10px', borderRadius: '8px' }}>
          <p style={{ color: '#666', marginBottom: '10px' }}>
            <strong>File:</strong> {originalFileName}<br/>
            <strong>Clean URL:</strong> http://localhost:5000/pdf/{originalFileName}
          </p>

          <Document
            file={fileUrl}
            onLoadSuccess={onDocumentLoadSuccess}
            onLoadError={(error) => console.error('PDF Error:', error)}
          >
            <Page 
              pageNumber={pageNumber}
              width={800}
            />
          </Document>

          {numPages && (
            <div style={{ textAlign: 'center', margin: '20px 0' }}>
              <button 
                onClick={() => setPageNumber(pageNumber - 1)}
                disabled={pageNumber <= 1}
                style={{ margin: '0 10px', padding: '8px 16px', backgroundColor: '#2196F3', color: 'white', border: 'none', borderRadius: '4px' }}
              >
                Previous
              </button>

              <span style={{ color: '#333', fontWeight: 'bold', margin: '0 20px' }}>
                Page {pageNumber} of {numPages}
              </span>

              <button 
                onClick={() => setPageNumber(pageNumber + 1)}
                disabled={pageNumber >= numPages}
                style={{ margin: '0 10px', padding: '8px 16px', backgroundColor: '#2196F3', color: 'white', border: 'none', borderRadius: '4px' }}
              >
                Next
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
