import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf'; // Fix PDF worker
import { ReactReader } from 'react-reader'; // EPUB support
import axios from 'axios';
import './App.css';
import * as pdfjsLib from 'pdfjs-dist';
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;
// Fix PDF.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

function App() {
  const [fileUrl, setFileUrl] = useState(null);
  const [highlightedText, setHighlightedText] = useState('');
  const [query, setQuery] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [error, setError] = useState('');
  const [pageNumber, setPageNumber] = useState(1);

  const handleFileUpload = async (event) => {
    const formData = new FormData();
    formData.append('file', event.target.files[0]);
    try {
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (!data.filePath) throw new Error('Invalid filePath from server');
      setFileUrl(`http://localhost:5000${data.filePath}`);
      setError('');
    } catch (error) {
      console.error('Upload failed:', error);
      setError('Upload failed. Check server and file format.');
    }
  };

  const handleTextSelection = () => {
    const selection = window.getSelection();
    if (selection.toString()) {
      console.log('Selected text:', selection.toString());
      setHighlightedText(selection.toString());
    }
  };

  const handleQuerySubmit = async () => {
    if (!highlightedText || !query) return;
    try {
      const response = await axios.post('http://localhost:5000/api/query', {
        text: highlightedText,
        prompt: query,
      });
      setAiResponse(response.data.response);
    } catch (error) {
      console.error('Query failed:', error);
      setAiResponse('Error fetching response');
    }
  };

  const onDocumentLoadSuccess = ({ numPages }) => {
    setPageNumber(1);
  };

  return (
    <div className="app">
      <h1>BookRead AI</h1>
      <input type="file" accept=".pdf,.epub" onChange={handleFileUpload} />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {fileUrl && (
        <div onMouseUp={handleTextSelection} className="content-area">
          {fileUrl.endsWith('.pdf') ? (
            <Document
              file={fileUrl}
              onLoadSuccess={onDocumentLoadSuccess}
              onLoadError={(err) => setError(`PDF load error: ${err.message}`)}
            >
              <Page
                pageNumber={pageNumber}
                width={600}
                height={800}
                renderTextLayer={true}
                renderAnnotationLayer={false}
              />
            </Document>
          ) : (
            <div style={{ width: '100%', height: 800 }}>
              <ReactReader
                url={fileUrl}
                location={'epubcfi(/6/2[cover]!/6)'}
                locationChanged={(loc) => console.log('EPUB location:', loc)}
              />
            </div>
          )}
        </div>
      )}
      <div className="sidebar">
        <h3>Highlighted Text</h3>
        <p>{highlightedText || 'Select text to highlight'}</p>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="e.g., Explain this"
        />
        <button onClick={handleQuerySubmit}>Ask AI</button>
        <h3>AI Response</h3>
        <p>{aiResponse || 'No response yet'}</p>
      </div>
    </div>
  );
}

export default App;
